--
-- Дамп данных таблицы `collections`
--

INSERT INTO `collections` (`id`, `name`, `description`, `category_id`) VALUES
(0, 'Домашние кинотеары', 'Приходя за телевизором в магазин электроники, покупатели неизбежно сталкиваются с проблемой выбора. В нашем интернет-магазине представлена масса телевизоров самых разных марок. Как разобраться во всем многообразии и выбрать именно ту модель, которая подходит Вам? На что стоит обратить внимание при выборе? Все эти насущные вопросы мы и рассмотрим в статье.', 'sound_systems;televizory'),
(1, 'Домашние кинотеатры', 'Домашние кинотеатры позволяют получить объемный звук высокого качества. Такие устройства являются комбинированными комплексами, состоящими из проигрывателя и акустической системы. Учитывая характерные особенности обеих составляющих, можно подобрать домашний кинотеатр с оптимальными параметрами для любых задач. Проигрыватель поддерживает различные форматы носителей информации. Большое количество совместимых стандартов делает модель универсальной, обеспечивая воспроизведение контента с разных устройств.', 'sound_systems'),
(2, 'Телевизоры', '3D телевизоры способны передавать объемное изображение, добиваясь его высокой реалистичности. Выбирая, какой 3D телевизор купить, следует в первую очередь определиться с такими характеристиками, как:активная или пассивная технология 3 Д. Первый вариант хорош тем, что лучше передает цвета, но сто́ит дороже. Модели с пассивной 3 Д технологией имеют более доступную цену. Диагональ экрана. При выборе нужно учитывать расстояние от зрителя до 3Д ТВ. Для просмотра с расстояния 2,5-3,5 м оптимальный вариант — 82 см. Разрешение. Напрямую влияет на качество изображения. 4К UHD — самое высокое разрешение из существующих сегодня.', 'televizory'),
(3, 'Планшеты', 'Статистика показывает, что покупатели все чаще выбирают для домашних и рабочих нужд планшет. Именно это устройство приходит на смену ноутбуку или системному блоку. Эти универсальные помощники есть в модельном ряду каждого крупного производителя. Давайте разберемся, какие моменты стоит учитывать при выборе подобного гаджета.', 'planshety'),
(4, 'Ноутбуки', 'Купить ноутбук очень просто: достаточно оформить заказ онлайн и выбрать подходящий способ оплаты. У нас возможна продажа ноутбуков в кредит. Консультанты нашего магазина всегда готовы прийти Вам на помощь – воспользовавшись их советами, Вы не только сможете выбрать для себя подходящий ноутбук, но и приобрести необходимые сопутствующие товары.', 'noutbuki');

